mapper.select:
  SELECT C_NO, C_TITLE, C_CONTENT, C_START, C_END, G_NO
FROM GALLERY.CALLENDAR
WHERE C_NO=#{c_no}

SELECT C_NO, C_TITLE, C_CONTENT, C_START, C_END, G_NO
FROM GALLERY.CALLENDAR
WHERE G_NO=#{g_no} AND SUBSTR(C_START,1,4)=#{c_start}

SELECT C_NO, C_TITLE, C_CONTENT, C_START, C_END, G_NO
FROM GALLERY.CALLENDAR
WHERE G_NO=#{g_no} AND C_START =#{c_start}

  SELECT C_NO, C_TITLE, C_CONTENT, C_START, C_END, G_NO
FROM GALLERY.CALLENDAR
WHERE G_NO=#{g_no}
